import React, { useEffect, useState } from 'react';
import {projectsData} from "../data/projectsData";

const Project = ({projectNumber}) => {
    const [currentProject, setCurrentProject] = useState(projectsData[projectNumber]);
    const [left, setLeft] = useState();
    const [top, setTop] = useState();
    const [size, setSize] = useState();

    useEffect(()=>{
        setLeft(Math.floor(Math.random() * 200  + 700) + "px");
        setTop(Math.floor(Math.random() * 200  + 150) + "px");
        setSize("scale(" + (Math.random()+0.7) + ")")
    },[]);
    return (
      <div className="project-main">
        <div className="project-content">
            <h1>{currentProject.title}</h1>
            <p>{currentProject.date}</p>
            <ul className="languages">
                {currentProject.languages.map((item)=>(
                     <li key={item}>{item}</li>
                ))}
            </ul>
        </div>

        <div className="img-content">
            <div className="img-container hover">
                <span>
                    <h3>{currentProject.title}</h3>
                    <p>{currentProject.infos}</p>
                </span>
                <img src={currentProject.img}
                 alt={currentProject.title}  
                 className="img"/>
            </div>
            <div className="button-container">
                <a href={currentProject.link} target="_blank" rel='noopener noreferrer'
                className='hover'>
                    <span className='button'>Voir le site</span></a>
            </div>
        </div>
        <span className="random-circle" style={{left,top,transform: size}}></span>
      </div>
    );
};

export default Project;
