import React from 'react';

const ContactForm = () => {

    const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();
    const formMessage = document.querySelector(".form-message");

    emailjs.sendForm(process.env.REACT_APP_SERVICE,
     process.env.REACT_APP_TEMPLATE, 
     form.current, 
      process.env.REACT_APP_KEY)
      .then((result) => {
          console.log(result.text);
          form.current.reset();//pour nettoyer le formulaire
            formMessage.innerHTML = "<p class='text-green-500'>Message envoyé avec Succes </p>";
            setTimeout(() => {
                formMessage.innerHTML = "";
            }, 2500);
      }, (error) => {
          console.log(error.text);
          formMessage.innerHTML = "<p class='text-red-500'>Message non envoyé</p>";
          setTimeout(() => {
              formMessage.innerHTML = "";
          }, 2500);
      });
    }
    return (
        <div className=' m-20 p-19 text-center'>
            <h1 className='font-bold text-xl'>Formulaire</h1>
            <form ref={form} onSubmit={sendEmail}>
            <div className='my-3'>
                <label className='pr-3'>Name</label>
                <input className='border border-1 border-gray-900 rounded-md focus:bg-gray-100 px-5 py-1 mx-4 my-2'  type="text" name="name" required/>
            </div>
            <div  className='my-3'>
                <label className='pr-3'>Email</label>
                <input className='border border-1 border-gray-900 rounded-md focus:bg-gray-100 px-5 py-1 mx-4 my-2' type="email" name="email" required/>
            </div>
            <div >
                <label className='pr-3'>Message</label>
                <textarea className='border border-1 border-gray-900 rounded-md focus:bg-gray-100 px-5 py-1 mx-4 my-2' name="message" required/>
            </div>
            <input className='border-2 px-3 text-white rounded-full bg-orange-500 focus:bg-white focus:text-gray-800 focus:border-orange-700' type="submit" value="Envoyer" />
         </form>

        <div className='form-message'>

        </div>
    </div>
    );
};

export default ContactForm;
