import React from 'react';

const SocialNetwork = () => {

    const anim = ()=>{
        const icons = document.querySelectorAll(".social-network a");

        icons.forEach((lin)=>{
            lin.addEventListener("mouseover", (e)=>{
                link.style.transform = `translate(${e.offsetX - 20}px , ${e.offsetY - 13}px`;
            })
        });
    }

    return (
        <div className="social-network">
            <ul className="content">
                <a href="https://www.facebook.com/sidymamadou.diallo.5" 
                target="_blank" 
                rel="noopener noreferrer" 
                className="hover"
                onMouseOver={anim}>
                    <li>
                        <i className='fab fa-facebook'></i>
                    </li>
                </a>
                <a href="https://twitter.com/i/flow/single_sign_on"
                 target="_blank" 
                 rel="noopener noreferrer" 
                 className="hover"
                 onMouseOver={anim}>
                    <li>
                        <i className='fab fa-twitter'></i>
                    </li>
                </a>
                <a href="https://www.instagram.com/entrepreneur_riche24/?hl=fr" 
                target="_blank" 
                rel="noopener noreferrer" 
                className="hover"
                onMouseOver={anim}>
                    <li>
                        <i className='fab fa-instagram'></i>
                    </li>
                </a>
            </ul>
        </div>
    );
};

export default SocialNetwork;
